apt_preference.body:
- type: apt_preference
	name: '${1:name}'
	pin: '${2:version 1.2.3}'
	pin_priority: ${3:700}
	action:
		- ${4:add}

bash.body:
- type: bash
	name: '${1:name}'
	code:
		${5:script commands}
	action:
		- ${2:run}

batch.body:
- type: batch
	name: '${1:name}'
	code: |
		${5:batch commands}
	action:
		- ${2:run}

chef_client_launchd.body:
- type: chef_client_launchd
	name: '${1:Run Chef Infra Client as a launchd daemon}'
	interval: ${2:30}
	action:
		- ${3:enable}

chef_client_scheduled_task.body:
- type: chef_client_scheduled_task
	name: '${1:Run Chef Infra Client as a scheduled task}'
	frequency: '${2:minute}'
	frequency_modifier: ${3:30}
	action:
		- ${4:add}

chocolatey_source.body:
- type: chocolatey_source
	name: '${1:name}'
	bypass_proxy: ${2:true}
	source: '${3:http://example.com/something}'
	action:
		- ${4:add}

csh.body:
- type: csh
	name: '${1:name}'
	code: |
		${5:script commands}
	action:
		- ${6:run}

inspec_waiver_file_entry.body:
- type: inspec_waiver_file_entry
	name: '${1:name}'
	backup: ${2:false}
	expiration: '${3:YYYY-MM-DD}'
	file_path: '${4:/etc/chef/inspec_waivers.yml}'
	justification: '${5:reason for waiver}'
	run_test: ${6:false}
	action:
		- ${7:add}

reboot.body:
- type: reboot
	name: '${1:name}'
	reason: '${3:reason}'
	delay_mins: ${4:minutes}
	action:
		- ${2:request_reboot}

windows_firewall_profile.body:
- type: windows_firewall_profile
	name: '${1:profile_name}'
	default_inbound_action: '${2:Block}'
	default_outbound_action: '${3:Allow}'
	allow_inbound_rules: ${4:true}
	display_notification: ${5:false}
	action:
		- ${6:enable}

windows_pagefile.body:
- type: windows_pagefile
	name: '${1:path}'
	automatic_managed: ${2:false}
	initial_size: ${3:0}
	maximum_size: ${4:0}
	action:
		- ${5:set}

windows_update_settings.body:
- type: windows_update_settings
	name: '${1:name}'
	disable_os_upgrades: ${2:false}
	elevate_non_admins: ${3:true}
	target_wsus_group_name: '${4:group_name}'
	wsus_server_url: '{$5:https://server_url}'
	block_windows_update_website: {$6:false}
	automatic_update_option: :{$7:download_and_schedule}
	automatically_install_minor_updates: {$8:false}
	enable_detection_frequency: {$9:false}
	custom_detection_frequency: {$10:22}
	no_reboot_with_users_logged_on: {$11:true}
	disable_automatic_updates: {$12:false}
	scheduled_install_day: '{$13:https://server_url}'
	scheduled_install_hour: {$14:23}
	update_other_ms_products: {$15:true}
	action:
		- ${5:set}
